    private void jButton25ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton25ActionPerformed
num2=Integer.parseInt(tablero.getText());

     switch(signo){
         case"+":
             tablero.setText(Double.toString(num1+num2));
             break;
         case"-":
             tablero.setText(Double.toString(num1-num2));
             break;
         case "*":
              tablero.setText(Double.toString(num1*num2));
              break;
         case "/":
              tablero.setText(Double.toString(num1/num2));
              break;
         case "sin":
             tablero.setText(Double.toString((double)  Math.sin(Math.toRadians(num1))));
             break;
         case "cos":
             tablero.setText(Double.toString((double)Math.cos(Math.toRadians(num1) )));
             break;
         case "tan":
             tablero.setText(Double.toString((double)Math.tan(Math.toRadians(num1))));
             break;
      
          
       
     }
     


    }//GEN-LAST:event_jButton25ActionPerformed

